class RunSmorkParticle extends THREE.Object3D{

    private particleGroup: any;
    private Untitled1Emitter: any;
    public mesh: THREE.Mesh;

    private alive: number = 0;
    private flag: boolean = false;

    private target: any;



    constructor(target:any) {

        super();

        this.target = target;

        this.visible = false;
        var particleScale: number = 3;


        // Create particle group
        this.particleGroup = new SPE.Group({
            texture: THREE.ImageUtils.loadTexture('img/smork0.png'),
            //texture: THREE.ImageUtils.loadTexture('data:image/png;base64,'),
            maxAge: 1.2,
            hasPerspective: 1,
            colorize: 1,
            transparent: 1,
            alphaTest: 0.5,
            depthWrite: false,
            depthTest: true,
            blending: THREE.NormalBlending
        });

        // Create particle emitter 0
        this.Untitled1Emitter = new SPE.Emitter({
            type: 'cube',
            particleCount: 10,
            position: new THREE.Vector3(0, 0, 0),
            positionSpread: new THREE.Vector3(.3, .3, .3),
            velocity: new THREE.Vector3(0, 0, 0),
            velocitySpread: new THREE.Vector3(0, 0, 0),
            acceleration: new THREE.Vector3(0, 0, 0),
            accelerationSpread: new THREE.Vector3(0, 0, 0),
            sizeStart: 1,
            sizeMiddle: 6 * particleScale,
            sizeEnd: 5 * particleScale,
            opacityStart: 0.8,
            opacityMiddle: 0.1,
            alive: 0.5,
            colorStart: new THREE.Color(0xffdba5),
            colorMiddle: new THREE.Color(0xffdfa1),
            colorEnd: new THREE.Color(0xffffff)
        });


        this.particleGroup.addEmitter(this.Untitled1Emitter);


        // Add mesh to your scene. Adjust as necessary.
        this.mesh = this.particleGroup.mesh;
        this.mesh.frustumCulled = false;    //カメラの範囲からmeshの原点が外れてもレンダリングする
        SceneManager.scene.add(this.mesh);

        //this.animate();
        Vars.setAnimateFunc(this.animate.bind(this));
    }


    private animate(): void {

        //requestAnimationFrame(() => this.animate());

        if (!this.visible) return;

        //set particle count
        this.setAlive();

        this.Untitled1Emitter.position.copy(this.target.position);

        this.particleGroup.tick(Vars.delta);

    }


    private time: number = 0;
    private interval: number = .1;
    private setAlive(): void {

        if (!this.flag && this.alive == 0 ||
            this.flag && this.alive == 1 ) return;

        this.time += Vars.delta;
        if (this.time > this.interval) {
            if (this.flag) {
                this.alive = 1;
            } else {

                this.alive -= .2;
                if (this.alive < 0) {
                    this.alive = 0;
                    setTimeout(function () {
                        this.visible = false
                    }.bind(this), 2000);
                    return;
                }

            }

            this.Untitled1Emitter.alive = this.alive;

            this.time = 0;
        }

    }


    public on(): void {

        this.visible = true;
        this.flag = true;

    }


    public off(): void {

        this.flag = false;

    }

}